import React from "react";

const projects = [
    {
        id: 1,
        title: "Manufacturing ERP System",
        image: "/assets/projects/smrfs/google-form-integration.gif",
        badges: ["#DOTNET", "#FULLSTACK"]
    },
    {
        id: 2,
        title: "Another Project",
        image: "/assets/projects/smrfs/google-form-integration.gif",
        badges: ["#REACT", "#NODEJS"]
    }
];

const Projects = () => {
    return (
        <section id="project">
            <h2 className="section__text__p1">Projects</h2>
            <span className="line"></span>
            <div className="project-container swiper">
                <div className="project-wrapper">
                    <div className="project-list swiper-wrapper">
                        {projects.map((project) => (
                            <div className="project-item swiper-slide" key={project.id}>
                                <a href="#" className="project-link">
                                    <img className="project-image" src={project.image} alt={project.title} />
                                    <div className="project-badge">
                                        {project.badges.map((badge, index) => (
                                            <p className="badge" key={index}>{badge}</p>
                                        ))}
                                    </div>
                                    <h2 className="project-title">{project.title}</h2>
                                    <button className="project-button"><i className="bi bi-arrow-down-right"></i></button>
                                </a>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </section>
    );
};

export default Projects;
