import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination } from "swiper/modules";
import { useRef, useEffect } from "react";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";

const projects = [
    { id: 1, title: "Manufacturing ERP", img: "/assets/projects/smrfs/google-form-integration.gif" },
    { id: 2, title: "Project 2", img: "/assets/projects/smrfs/google-form-integration.gif" },
    { id: 3, title: "Project 3", img: "/assets/projects/smrfs/google-form-integration.gif" },
];

const Projects = () => {
    const swiperRef = useRef(null);

    useEffect(() => {
        if (swiperRef.current) {
            swiperRef.current.swiper.loop = true; // Ensure looping is enabled
        }
    }, []);

    return (
        <section id="project">
            <h2 className="section__text__p1">Projects</h2>
            <span className="line"></span>

            <Swiper
                ref={swiperRef}  // Attach ref to Swiper
                className="project-wrapper"
                modules={[Navigation, Pagination]}
                loop={true}
                spaceBetween={20}
                pagination={{ clickable: true, dynamicBullets: true }}
                navigation
                breakpoints={{
                    0: { slidesPerView: 1 },
                    900: { slidesPerView: 2 },
                    1400: { slidesPerView: 3 },
                }}    
            >
                {projects.map((project) => (
                    <SwiperSlide key={project.id}>
                        <a className="project-link" href="">
                            <img className="project-image" src={project.img} alt={project.title} />
                            <div className="project-badge">
                                <p className="badge dotnet-badge">#DOTNET</p>
                                <p className="badge fullstack-badge">#FULLSTACK</p>
                            </div>
                            <h2 className="project-title">{project.title}</h2>
                        </a>
                    </SwiperSlide>
                ))}                    
            </Swiper>
        </section>
    );
};

export default Projects;
